import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import pg from "../../assets/audio/kuis/pagelaranwayang.mp3";
import pg1 from "../../assets/image/kuis/pg1.png";
import pg2 from "../../assets/image/kuis/pg2.png";
import pg3 from "../../assets/image/kuis/pg3.png";
import pg4 from "../../assets/image/kuis/pg4.png";

const shuffleArray = (array) => {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
};

const Swara = ({ nextPagePath }) => {
  const navigate = useNavigate();
  const originalSong = [
    {
      audioSrc: pg,
      correctAnswer: "option4",
      options: [
        { id: "option1", imgSrc: pg2 },
        { id: "option2", imgSrc: pg3 },
        { id: "option3", imgSrc: pg4 },
        { id: "option4", imgSrc: pg1 },
      ],
    },
  ];

  const [shuffledSong, setShuffledSong] = useState(
    originalSong.map((song) => ({
      options: shuffleArray([...song.options]),
    }))
  );

  const [isPlaying, setIsPlaying] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [isPopupVisible, setIsPopupVisible] = useState(false);
  const [showHomeButton, setShowHomeButton] = useState(false);

  const playAudio = () => {
    const audio = new Audio(originalSong[0].audioSrc);
    audio.play();
    setIsPlaying(true);
    audio.onended = () => {
      setIsPlaying(false);
    };
  };

  const handleChoiceClick = (optionId) => {
    if (selectedAnswer) return; // Prevent clicking after an answer is selected

    setSelectedAnswer(optionId);
    if (optionId === originalSong[0].correctAnswer) {
      setFeedback("Bener!");
      setScore((prev) => prev + 1); // Increment score
      setShowHomeButton(true);
    } else {
      setFeedback("Coba Maneh Yok!");
    }

    // Show popup after the user selects an answer
    setIsPopupVisible(true);
  };

  const goToHomePage = () => {
    navigate("/aksaranglegena/kuis1");
  };

  const handlePopupAction = () => {
    setIsPopupVisible(false);
    navigate(nextPagePath);
  };

  return (
    <div className="sound-match-game">
      <h1>Nyocokake Swara 1</h1>
      <div className="play-button-container">
        <button
          className="play-button"
          onClick={playAudio}
          disabled={isPlaying}
        >
          <i className="play-icon"></i>
        </button>
      </div>
      <div className="image-options">
        {shuffledSong[0].options.map((option) => (
          <img
            key={option.id}
            src={option.imgSrc}
            alt={`Option ${option.id}`}
            onClick={() => handleChoiceClick(option.id)}
            className={`image-option ${
              selectedAnswer === option.id ? "selected" : ""
            }`}
          />
        ))}
      </div>

      {feedback && <p className="feedback">{feedback}</p>}
      {showHomeButton && (
        <button className="home-button" onClick={goToHomePage}>
          Kembali Ke Kuis
        </button>
      )}

      {isPopupVisible && (
        <div className="popup">
          <h2>Nilai: {score}</h2>
          <p>{feedback === "Bener!" ? "Kerja Bagus!" : "Coba Maneh Yok!"}</p>
          <button onClick={handlePopupAction}>Lanjutkan</button>
        </div>
      )}
    </div>
  );
};

export default Swara;
