import ha from "../../assets/image/aksara1/ha.png";
import ra from "../../assets/image/aksara1/ra.png";
import sa from "../../assets/image/aksara1/sa.png";
import ya from "../../assets/image/aksara1/ya.png";
import ba from "../../assets/image/aksara1/ba.png";
import ja from "../../assets/image/aksara1/ja.png";
import cover1 from "../../assets/image/Nglegena.png";
import { useEffect, useState } from "react";
import "../../style/memorycard.css";
import { useNavigate } from "react-router-dom";

const MemoryGames1 = ({ nextPagePath }) => {
  const navigate = useNavigate();
  const cardImage = [
    { image: ha },
    { image: ra },
    { image: sa },
    { image: ya },
    { image: ba },
    { image: ja },
  ];

  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiceTwo, setChoiceTwo] = useState(null);
  const [flippedCards, setFlippedCards] = useState([]);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [timer, setTimer] = useState(null);
  const [isPopupVisible, setIsPopupVisible] = useState(false);
  const [score, setScore] = useState(0);

  const shuffleCards = () => {
    const shuffledCards = [...cardImage, ...cardImage]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({ ...card, id: Math.random() }));

    setCards(shuffledCards);
    setTurns(0);
    setFlippedCards([]);
    setElapsedTime(0);
    setScore(0);
    setIsPopupVisible(false);
    startTimer(); // Start the timer when shuffling
  };

  const handleClick = (card) => {
    if (flippedCards.includes(card.id) || choiceTwo) return;
    if (choiceOne) {
      setChoiceTwo(card);
      setFlippedCards((prev) => [...prev, card.id]);
    } else {
      setChoiceOne(card);
      setFlippedCards((prev) => [...prev, card.id]);
    }
  };

  useEffect(() => {
    if (choiceOne && choiceTwo) {
      if (choiceOne.image === choiceTwo.image) {
        resetTurn();
      } else {
        setTimeout(() => {
          setFlippedCards((prev) =>
            prev.filter((id) => id !== choiceOne.id && id !== choiceTwo.id)
          );
          resetTurn();
        }, 1000);
      }
    }
  }, [choiceOne, choiceTwo]);

  const resetTurn = () => {
    setChoiceOne(null);
    setChoiceTwo(null);
    setTurns((prevTurns) => prevTurns + 1);

    // Check if all cards are flipped
    if (flippedCards.length === cards.length) {
      clearInterval(timer); // Stop the timer
      calculateScore(); // Calculate score when all pairs are found
    }
  };

  const startTimer = () => {
    const newTimer = setInterval(() => {
      setElapsedTime((prev) => prev + 1);
    }, 1000);
    setTimer(newTimer);
  };

  const calculateScore = () => {
    const timePenalty = Math.floor(elapsedTime * 2);
    const turnPenalty = turns * 2;
    const finalScore = Math.max(0, 500 - timePenalty - turnPenalty);

    setScore(finalScore);
    setIsPopupVisible(true);
  };

  const handlePopupAction = (action) => {
    setIsPopupVisible(false);
    if (action === "next") {
      navigate(nextPagePath);
    } else {
      navigate("/games/memory");
    }
  };

  useEffect(() => {
    return () => clearInterval(timer); // Cleanup on unmount
  }, [timer]);

  return (
    <div className="card-header">
      <h1 className="card-title">Memory 1</h1>
      <button className="card-click-button" onClick={shuffleCards}>
        Atur Kartu
      </button>
      <div className="timer">
        <h3>Waktu: {elapsedTime} detik</h3>
      </div>
      <div className="card-grid">
        {cards.map((card) => (
          <div className="card" key={card.id}>
            <div className="card-image">
              {flippedCards.includes(card.id) ? (
                <img src={card.image} alt="card front" />
              ) : (
                <img
                  src={cover1}
                  onClick={() => handleClick(card)}
                  alt="cover"
                />
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Popup for score */}
      {isPopupVisible && (
        <div className="memory-popup">
          <h2>Nilai: {score}</h2>
          <button
            className="memory-popup-button"
            onClick={() => handlePopupAction("back")}
          >
            Kembali
          </button>
          <button
            className="memory-popup-button"
            onClick={() => handlePopupAction("next")}
          >
            Level Selanjutnya
          </button>
        </div>
      )}
    </div>
  );
};

export default MemoryGames1;
