import ha from "../../assets/image/aksara1/ha.png";
import ra from "../../assets/image/aksara1/ra.png";
import sa from "../../assets/image/aksara1/sa.png";
import ya from "../../assets/image/aksara1/ya.png";
import ba from "../../assets/image/aksara1/ba.png";
import { useState } from "react";

const cardImage = [
  { image: ha },
  { image: ra },
  { image: sa },
  { image: ya },
  { image: ba },
];

const [cards, setCards] = useState([]);
const [turns, setTurns] = useState(0);

function memory1() {
  const shuffleCards = () => {
    const shuffleCards = [...cardImage, ...cardImage]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({ ...card, id: Math.random }));

    setCards(shuffleCards);
    setTurns(0);
  };

  return (
    <div>
      <h1>Memory 1</h1>
      <button onClick={shuffleCards}>Atur Kartu</button>

      <div>
        {cards.map((card) => (
          <div key={card.id}>
            <div>
              <img src={card.image} alt="card front" />
              <img src="../../assets/image/nglegena" alt="cover" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default memory1;
