import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import wk from "../../assets/image/kuis/wayangkulit1.png";
import wk1 from "../../assets/image/kuis/wayangkulit.jpg";
import wkl from "../../assets/image/kuis/wayangklithik.jpg";
import ww from "../../assets/image/kuis/wayangwong.jpg";
import wg from "../../assets/image/kuis/wayanggolek.jpg";
import "../../style/ImageMatch.css";

// Shuffle function
const shuffleArray = (array) => {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
};

const NyocokakeGambar = ({ nextPagePath }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { userId } = location.state || {};

  const originalPairs = [
    {
      clueImage: wk,
      options: [
        { id: 1, image: ww, isCorrect: false },
        { id: 2, image: wg, isCorrect: false },
        { id: 3, image: wk1, isCorrect: true },
        { id: 4, image: wkl, isCorrect: false },
      ],
    },
  ];

  // State for shuffled pairs, initialized only once
  const [shuffledPairs, setShuffledPairs] = useState(
    originalPairs.map((pair) => ({
      clueImage: pair.clueImage,
      options: shuffleArray([...pair.options]), // Shuffle options for each pair
    }))
  );

  const [currentPairIndex, setCurrentPairIndex] = useState(0);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isAnswerCorrect, setIsAnswerCorrect] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0); // Stopwatch time
  const [isPopupVisible, setIsPopupVisible] = useState(false);
  const [finalScore, setFinalScore] = useState(0);
  const [timer, setTimer] = useState(null); // Timer reference

  const currentPair = shuffledPairs[currentPairIndex];

  // Stopwatch effect
  useEffect(() => {
    const interval = setInterval(() => {
      setElapsedTime((prev) => prev + 1000); // Increment by 1000ms (1 second)
    }, 1000);
    setTimer(interval); // Store timer reference

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const handleImageClick = async (option) => {
    setSelectedImage(option.id);
    setIsAnswerCorrect(option.isCorrect);

    if (option.isCorrect) {
      // Stop timer and handle score when the answer is correct
      clearInterval(timer); // Stop the timer

      if (currentPairIndex < shuffledPairs.length - 1) {
        // Move to the next question
        setSelectedImage(null);
        setIsAnswerCorrect(null);
        setCurrentPairIndex(currentPairIndex + 1);
      } else {
        // Calculate score and show popup if it's the last question
        const finalElapsedTime = (elapsedTime / 1000).toFixed(2); // Convert to seconds
        const calculatedScore = calculateScore(finalElapsedTime);
        setFinalScore(calculatedScore);
        setIsPopupVisible(true); // Show the popup with the score

        try {
          await axios.post("/api/score", {
            userId,
            gameId: "g1",
            score: calculatedScore,
          });
        } catch (error) {
          console.error("Error saving score:", error);
        }
      }
    }
  };

  const calculateScore = (duration) => {
    return Math.max(0, 100 - Math.floor(duration * 0.5));
  };

  const handlePopupAction = (action) => {
    setIsPopupVisible(false);
    if (action === "next") {
      navigate(nextPagePath); // Navigate to the next level
    } else {
      navigate(-1); // Go back to the previous page
    }
  };

  return (
    <div className="image-cover">
      <div className="image-clue-container">
        <h1 className="image-clue-container-header">Nyocokake Gambar 1</h1>
        <h2 className="image-clue-container-header1">
          Pilih gambar sing cocog karo aksara
        </h2>

        <img
          src={currentPair.clueImage}
          alt="Clue"
          className="image-clue-image"
        />
        <div className="option image-container">
          {currentPair.options.map((option) => (
            <div key={option.id} className="option-image-container">
              <img
                src={option.image}
                alt="Option"
                className={selectedImage === option.id ? "selected" : ""}
                onClick={() => handleImageClick(option)}
              />
            </div>
          ))}
        </div>

        {isAnswerCorrect !== null && (
          <div>
            <p
              className={`feedback-message ${
                isAnswerCorrect ? "" : "incorrect"
              }`}
            >
              {isAnswerCorrect ? "Bener!" : "Coba Maneh Yok!"}
            </p>
          </div>
        )}
      </div>

      <div className="image-score-container">
        <h3>Waktu: {(elapsedTime / 1000).toFixed(2)} detik</h3>
      </div>

      {isPopupVisible && (
        <div className="image-popup">
          <h2>Nilai: {finalScore}</h2>
          <p>Kerja Bagus!</p>
          <button onClick={() => handlePopupAction("back")}>Kembali</button>
          <button onClick={() => handlePopupAction("next")}>
            Level Selanjutnya
          </button>
        </div>
      )}
    </div>
  );
};

export default NyocokakeGambar;
